class Dashboard extends Component {
state = {
    data: [],
    defaultSocialMedia: 'Twitter',
    messageTerm: 'Tweets',
    socialMedia: ['Twitter', 'Facebook', 'Instagram'],
    tweetnReplies: [],
    appHeaderStyle: 'twit-class',
    dataState: '',
    tableHead: ['Tweets', 'User', 'Time'],
  };


  componentDidMount() {
    // if (!LocalState.checkTokenValidity()) {
    //   return this.props.history.push('/');
    // }

    this.setState({dataState: 'Fetching'});
    // Call our fetch function below once the component mounts
    this.callBackendAPI()
      .then(res => {
        this.setState({dataState: 'Sorting'});
        this.setState({
          tweetnReplies: this.sortFetchedData(res.replies)
        })
      })
      .catch(err => console.log(err));
  }

   sortFetchedData = tweets => {
    let sortedData = [];
    let duplicateData = [];

    tweets.forEach(obj => {
      sortedData = [...sortedData, obj];
      let lastChild = sortedData[sortedData.length - 1];
      lastChild.replies = [];
      for (let i=0; i < tweets.length; i++) {
        if (tweets[i].in_reply_to_status_id_str === obj.id_str) {
          sortedData = [...sortedData, 
            lastChild.replies= [tweets[i], ...lastChild.replies]
          ]
          duplicateData.push(tweets[i].id);
        }
      };
      
      // remove entries that are duplicateDataay instead of object
      sortedData = sortedData.filter(data => data.length == undefined);
    });

    duplicateData.forEach(entry => {
      sortedData = sortedData.filter(data => data.id != entry)
    })

    this.setState({dataState: ''});
    return sortedData;
  }

  // Fetches our GET route from the Express server. (Note the route we are fetching matches the GET route from server.js
  callBackendAPI = async () => {
    const response = await fetch('/api/v1/feeds');
    const body = await response.json();
    if (response.status !== 200) {
      throw Error(body.message) 
    }
    return body;
  };

  setAttributeToState = (color, socialMedia) => this.setState({
    appHeaderStyle: color,
    mediaSelected: socialMedia,
  })

  handleSocialMediaSelection = (event) => {
    let color;
    switch(event.target.value) {
      case 'Facebook':
        this.setAttributeToState('fb-class', 'facebook');
        break;
      case 'Instagram':
        this.setAttributeToState('insta-class', 'instagram');
        break;
      default:
        this.setAttributeToState('twit-class', 'twitter');
    }
    this.callBackendAPI();
  }

  uncollapseTableRows = () => {
  }

  handleLogout = () => {
    LocalState.removeToken();
    this.props.history.push('/');
  }

  render() {
    const { dataState, socialMedia, messageTerm, defaultSocialMedia, tweetnReplies, appHeaderStyle, tableHead } = this.state;
    
    return (
      <Fragment>
        <Header
          messageTerm="Tweets"
          pageType="one"
        />
        {
           dataState
           ? <Spinner displayText={dataState} />
           : <Table
              pageType="one"
              content={
                <TabulateData
                  pageType
                  tweets={tweetnReplies}
                />
              }
              tableHead={tableHead}
            />
        }
      </Fragment>
    );
  }
}

export default Dashboard;